import React, { useState } from "react";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onFilterChange={filterChangeHandler}
        />
        {props.items.map((expense) => (
          <ExpenseItem />
        ))}
        <ExpenseItem
          title={props.expensesVal[0].title}
          amount={props.expensesVal[0].amount}
          date={props.expensesVal[0].date}
        ></ExpenseItem>
        <ExpenseItem
          title={props.expensesVal[1].title}
          amount={props.expensesVal[1].amount}
          date={props.expensesVal[1].date}
        ></ExpenseItem>
        <ExpenseItem
          title={props.expensesVal[2].title}
          amount={props.expensesVal[2].amount}
          date={props.expensesVal[2].date}
        ></ExpenseItem>
        <ExpenseItem
          title={props.expensesVal[3].title}
          amount={props.expensesVal[3].amount}
          date={props.expensesVal[3].date}
        ></ExpenseItem>
      </Card>
    </div>
  );
};

export default Expenses;
